queries.metadata_comprehensive:
query metadata_comprehensive {
  publishedDatasources {
    id
    luid
    name
    description
    hasExtracts

    owner {
      luid
      username
      email
    }

    isCertified
    certifier {
      luid
      username
      email
    }
    certificationNote

    fields {
      id
      name
      description

      ... on CalculatedField {
        formula
        description
        dataType
      }
    }

    upstreamDatasources {
      name
      luid
      id
      owner {
        luid
        username
        email
      }
    }

    downstreamWorkbooks {
      name
      luid
      id
      owner {
        luid
        username
        email
      }

      embeddedDatasources {
        name
        fields {
          name
          description
          ... on CalculatedField {
            formula
            dataType
          }
        }
      }
    }
  }

  workbooks {
    name
    luid
    id
    owner {
      luid
      username
      email
    }

    embeddedDatasources {
      name
      fields {
        name
        description
        ... on CalculatedField {
          formula
          dataType
        }
      }
    }
  }
}

queries.published_datasources:
query published_datasources {
  publishedDatasources {
    id
    luid
    name
    hasUserReference
    hasExtracts
    extractLastRefreshTime
    site {
      luid
    }
    fields {
      id
      name
      description
      ... on CalculatedField {
        formula
        dataType
      }
    }
    projectName
    projectVizportalUrlId
    owner {
      luid
      username
      email
    }
    isCertified
    certifier {
      luid
      username
      email
    }
    certificationNote
    description
  }
}
